'use strict';

// properties
const speed = 1.0; // match this to the shake speed
const offset = 0.35; // 0 to 1, point in time to start breathing
const duration = 0.35; // 0 to 1, percentage of time to breathe

// constants
const twoPI = 6.283185307179586476925286766559;
const periodTotal = twoPI / speed;
const phase = offset / speed * twoPI;
const periodOn = duration * periodTotal;


/**
 * @param {Number} value (for property 'visible')
 */
export function init(value) {
	thisLayer.pause();

	return value;
}

export function update(value) {
	// Enable the emitter every  seconds for half a second
	if ((engine.runtime - phase) % periodTotal < periodOn)
		thisLayer.play();
	else
		thisLayer.pause();

	thisObject.speed = 10;
	thisObject.colorn = 0.5;
	thisObject.lifetime = 0;

	return value;
}

